import './App.css';
import bg from './img/main-bg.jpg';
import {NavDropDown, Nav, Container, Navbar} from 'react-bootstrap';
import { useEffect } from 'react';
import './../node_modules/bootstrap/dist/css/bootstrap.min.css';

const Header =()=> {
  useEffect(()=>{
    document.getElementById('bg').style.backgroundImage = `url('${bg}')`;
  });
  return (
    <div id='bg' style={{height: '400px', backgroundSize: 'cover'}}>
      <header className="custom d-flex justify-content-around">
      <Container className='d-flex justify-content-between'>
        <div class='d-block'>
          <h1>Cozy house</h1>
          <h6>Shelter for pets in boston</h6>
        </div>
                  <Nav>
                  <Nav.Link href='#'>About the shelter</Nav.Link>
                  <Nav.Link href='#'>Our pets</Nav.Link>
                  <Nav.Link href='#'>Help the shelter</Nav.Link>
                  <Nav.Link href='#'>Contacts</Nav.Link>
                  </Nav>
      </Container>
      </header>
    </div>
  );
}

export default Header;
